// A macro for the Foundry virtual tabletop that lets a user configure their token's vision and lighting settings. The original script is taken from Sky's foundry repo here: https://github.com/Sky-Captain-13/foundry/blob/master/scriptMacros/tokenVision.js. Modified by Darthbanjo for Star Wars. 

let dialogEditor = new Dialog({
  title: `Apply Lightsaber Glow to Actor`,
  buttons: {
    none: {
      label: `None`,
      callback: () => {
        token.document.update({light:{ dim: 0, bright: 0, color: "00000", alpha: 1, animation:{ type: "none"}, coloration: 1}});
        dialogEditor.render(true);
      }
    },
blueblade: {
      label: `Blue`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright: .25, color : "#0080ff", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
    greenblade: {
      label: `Green`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright: .25, color : "#00cd00", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
redblade: {
      label: `Red`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright : .25, color : "#ff0000", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
purple: {
      label: `Violet`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright : .25, color : "#ff00ff", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },

yellow: {
      label: `Yellow`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright : .25, color : "#ffff00", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
darksaber: {
      label: `Darksaber/Grey`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright : .25, color : "#ffffff", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
orange: {
      label: `Orange`,
      callback: () => {
        token.document.update({light:{ dim: 1, bright : .25, color : "#ffa500", alpha: 0.5, angle: 360, animation:{ type: "torch", speed: 3, intensity: 3}, coloration: 1}});
        dialogEditor.render(true);
      }
   },
    close: {
      icon: "<i class='fas fa-tick'></i>",
      label: `Exit`
    },
  },
  default: "close",
  close: () => {}
});
dialogEditor.render(true)
